let currentSlide=0,totalSlides=0;function copyToClipboard(text){navigator.clipboard.writeText(text).then(()=>showNotification('Copied to clipboard! âœ¨')).catch(err=>{console.error('Could not copy text: ',err);const textArea=document.createElement('textarea');textArea.value=text;document.body.appendChild(textArea);textArea.select();document.execCommand('copy');document.body.removeChild(textArea);showNotification('Copied to clipboard! âœ¨');})}function showNotification(message){const notification=document.createElement('div');notification.textContent=message;notification.style.cssText='position:fixed;top:20px;right:20px;background:linear-gradient(45deg,#00f0ff,#ff006e);color:white;padding:15px 25px;border-radius:25px;font-weight:bold;z-index:1000;animation:slideInRight 0.5s ease,fadeOut 0.5s ease 2.5s;box-shadow:0 0 20px rgba(0,240,255,0.5)';document.body.appendChild(notification);setTimeout(()=>{if(notification.parentNode)notification.parentNode.removeChild(notification)},3000)}function initCarousel(){const track=document.getElementById('carouselTrack'),prevBtn=document.getElementById('prevBtn'),nextBtn=document.getElementById('nextBtn'),slides=document.querySelectorAll('.carousel-slide');if(!track||!prevBtn||!nextBtn)return;totalSlides=slides.length;function updateCarousel(){track.style.transform=`translateX(${-currentSlide*100}%)`}function nextSlide(){currentSlide=(currentSlide+1)%totalSlides;updateCarousel()}function prevSlide(){currentSlide=(currentSlide-1+totalSlides)%totalSlides;updateCarousel()}nextBtn.addEventListener('click',nextSlide);prevBtn.addEventListener('click',prevSlide);setInterval(nextSlide,5000);let startX=0,endX=0;track.addEventListener('touchstart',e=>startX=e.touches[0].clientX);track.addEventListener('touchend',e=>{endX=e.changedTouches[0].clientX;const diff=startX-endX;if(Math.abs(diff)>50)diff>0?nextSlide():prevSlide()})}function initScrollAnimations(){const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('fade-in-up');observer.unobserve(entry.target)}})},{threshold:0.1,rootMargin:'0px 0px -50px 0px'});document.querySelectorAll('section').forEach(section=>observer.observe(section))}function initParallax(){const gridBackground=document.querySelector('.neon-grid-background');if(gridBackground)window.addEventListener('scroll',()=>{const scrolled=window.pageYOffset,rate=scrolled*-0.5;gridBackground.style.transform=`translateY(${rate}px)`})}function addDynamicStyles(){const style=document.createElement('style');style.textContent='@keyframes slideInRight{from{transform:translateX(100%);opacity:0}to{transform:translateX(0);opacity:1}}@keyframes fadeOut{to{opacity:0;transform:translateX(100%)}}.carousel-image{transition:all 0.3s ease}.carousel-image:hover{transform:scale(1.05) rotate(1deg);filter:brightness(1.1) contrast(1.1)}';document.head.appendChild(style)}document.addEventListener('DOMContentLoaded',function(){console.log('ðŸš€ BOP website loaded successfully with cyberpunk enhancements!');initCarousel();initScrollAnimations();initParallax();addDynamicStyles();const yearElement=document.getElementById('current-year');if(yearElement)yearElement.textContent=new Date().getFullYear();document.querySelectorAll('a[href^="#"]').forEach(link=>{link.addEventListener('click',function(e){e.preventDefault();const target=document.querySelector(this.getAttribute('href'));if(target)target.scrollIntoView({behavior:'smooth'})})});document.querySelectorAll('.hover-animate').forEach(img=>{img.addEventListener('mouseenter',function(){this.style.filter='brightness(1.2) saturate(1.3) drop-shadow(0 0 20px #00f0ff)'});img.addEventListener('mouseleave',function(){this.style.filter='brightness(1) saturate(1) drop-shadow(none)'})});document.querySelectorAll('.carousel-image').forEach((img,index)=>img.style.animationDelay=`${index*0.1}s`);console.log('âœ¨ All BOP enhancements initialized!')});